using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class EncryptionHelper\n{\n    public static string EncryptString(string plainText, string key)\n    {\n        using (Aes aesAlg = Aes.Create())\n        {\n            aesAlg.Key = Encoding.UTF8.GetBytes(key);\n            aesAlg.Mode = CipherMode.CBC;\n            aesAlg.Padding = PaddingMode.PKCS7;\n\n            aesAlg.GenerateIV();\n            byte[] iv = aesAlg.IV;\n            string ivString = BitConverter.ToString(iv).Replace("-", "");\n\n            ICryptoTransform encryptor = aesAlg.CreateEncryptor(aesAlg.Key, aesAlg.IV);\n\n            using (MemoryStream msEncrypt = new MemoryStream())\n            {\n                using (CryptoStream csEncrypt = new CryptoStream(msEncrypt, encryptor, CryptoStreamMode.Write))\n                {\n                    using (StreamWriter swEncrypt = new StreamWriter(csEncrypt))\n                    {\n                        swEncrypt.Write(plainText);\n                    }\n                    byte[] encrypted = msEncrypt.ToArray();\n                    string encryptedString = BitConverter.ToString(encrypted).Replace("-", "");\n                    return ivString + encryptedString;\n                }\n            }\n        }\n    }\n    \n    public static void Main()\n    {\n        string key = "your-32-byte-key-here";\n        string plainText = "your-plain-text-here";\n        \n        string encryptedString = EncryptString(plainText, key);\n        Console.WriteLine("Encrypted String: " + encryptedString);\n    }\n}