using System;\nusing System.Collections.Generic;\nusing System.Data.SqlClient;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string query = @"\n            SELECT 'A' AS Source, ColumnA AS ColumnValue FROM TableA\n            UNION ALL\n            SELECT 'B' AS Source, ColumnB AS ColumnValue FROM TableB\n            UNION ALL\n            SELECT 'C' AS Source, ColumnC AS ColumnValue FROM TableC";\n        \n        var listA = new List<string>();\n        var listB = new List<string>();\n        var listC = new List<string>();\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand command = new SqlCommand(query, connection);\n            connection.Open();\n            SqlDataReader reader = command.ExecuteReader();\n\n            while (reader.Read())\n            {\n                string source = reader["Source"].ToString();\n                string value = reader["ColumnValue"].ToString();\n\n                switch (source)\n                {\n                    case "A":\n                        listA.Add(value);\n                        break;\n                    case "B":\n                        listB.Add(value);\n                        break;\n                    case "C":\n                        listC.Add(value);\n                        break;\n                }\n            }\n\n            reader.Close();\n        }\n\n        // Now listA, listB, and listC contain the data from each table.\n    }\n}