// Page A: Code-behind to register the script\n   protected void Page_Load(object sender, EventArgs e)\n   {\n       if (!IsPostBack)\n       {\n           string orderId = Request.QueryString["OrderId"];\n           if (!string.IsNullOrEmpty(orderId))\n           {\n               string script = $"<script type='text/javascript'>scrollToOrder('{orderId}');</script>";\n               ClientScript.RegisterStartupScript(this.GetType(), "ScrollToOrder", script);\n           }\n       }\n   }