void SpawnNewProcess()\n{\n    string fileName = GetFileName();\n    System.Diagnostics.Process proc = new System.Diagnostics.Process();\n    proc.StartInfo.FileName = fileName;\n    proc.Exited += new EventHandler(ProcessExited);\n    proc.EnableRaisingEvents = true;\n    proc.Start();\n}\n\nprivate void ProcessExited(Object source, EventArgs e)\n{\n    System.Diagnostics.Process proc = (System.Diagnostics.Process)source;\n    Console.WriteLine($"Process {proc.Id} has exited.");\n    // You can also access other properties of the process here, e.g. proc.StartInfo.FileName\n}