using System.ServiceModel;\n\npublic class RemoteCommandServiceProxy\n{\n    public IRemoteCommandService CreateProxy()\n    {\n        var binding = new BasicHttpBinding();\n        var endpoint = new EndpointAddress("http://localhost:8080/RemoteCommandService");\n        var channelFactory = new ChannelFactory<IRemoteCommandService>(binding, endpoint);\n        return channelFactory.CreateChannel();\n    }\n}