using System.Net.Http;\nusing HtmlAgilityPack;\nusing System.Threading.Tasks;\n\npublic async Task<string> ScrapeWebsiteAsync(string url)\n{\n    using (HttpClient client = new HttpClient())\n    {\n        HttpResponseMessage response = await client.GetAsync(url);\n        response.EnsureSuccessStatusCode();\n        \n        string pageContents = await response.Content.ReadAsStringAsync();\n        \n        HtmlDocument htmlDoc = new HtmlDocument();\n        htmlDoc.LoadHtml(pageContents);\n        \n        // Perform HTML parsing and navigation here\n        // Example: var node = htmlDoc.DocumentNode.SelectSingleNode("//a[@class='some-class']");\n        \n        return pageContents;\n    }\n}