public abstract class Parent\n{\n    public abstract string Foo { get; }\n}\n\npublic class ReadOnlyChild : Parent\n{\n    private string _foo = "ReadOnly Value";\n\n    public override string Foo\n    {\n        get { return _foo; }\n    }\n}\n\npublic class ReadWriteChild : Parent\n{\n    private string _foo;\n\n    public new string Foo\n    {\n        get { return _foo; }\n        set { _foo = value; }\n    }\n\n    public override string GetFoo()\n    {\n        return Foo;\n    }\n}