<%@ WebHandler Language="C#" Class="RenderControlHandler" %>\n   using System;\n   using System.Web;\n   using System.Web.UI;\n\n   public class RenderControlHandler : IHttpHandler\n   {\n       public void ProcessRequest(HttpContext context)\n       {\n           context.Response.ContentType = "text/html";\n           string controlPath = "~/MyControl.ascx";\n           UserControl userControl = (UserControl)context.Handler.LoadControl(controlPath);\n\n           // Optionally, set properties or state here\n           // userControl.SomeProperty = context.Request["SomeParameter"];\n\n           System.Text.StringBuilder sb = new System.Text.StringBuilder();\n           using (System.IO.StringWriter tw = new System.IO.StringWriter(sb))\n           {\n               using (HtmlTextWriter hw = new HtmlTextWriter(tw))\n               {\n                   userControl.RenderControl(hw);\n                   context.Response.Write(sb.ToString());\n               }\n           }\n       }\n\n       public bool IsReusable\n       {\n           get { return false; }\n       }\n   }