using System;\n\npublic class TaskScheduler\n{\n    public static DateTime CalculateNextRunTime(DateTime seedTime, TimeSpan frequency)\n    {\n        DateTime now = DateTime.Now;\n\n        if (now < seedTime)\n        {\n            throw new ArgumentException("Seed time cannot be in the future.");\n        }\n\n        // Calculate the number of intervals that have passed since the seed time\n        double intervalsPassed = (now - seedTime).TotalMilliseconds / frequency.TotalMilliseconds;\n        int completeIntervalsPassed = (int)Math.Floor(intervalsPassed);\n\n        // Calculate the next run time\n        DateTime nextRunTime = seedTime.AddMilliseconds((completeIntervalsPassed + 1) * frequency.TotalMilliseconds);\n        \n        return nextRunTime;\n    }\n\n    public static void Main()\n    {\n        DateTime seedTime = new DateTime(2023, 1, 1, 0, 0, 0); // Example seed time\n        TimeSpan frequency = TimeSpan.FromHours(5); // Example frequency (5 hours)\n\n        DateTime nextRunTime = CalculateNextRunTime(seedTime, frequency);\n        Console.WriteLine("Next run time: " + nextRunTime);\n    }\n}