public class Example\n{\n    public static void Main()\n    {\n        // First call, method is JIT compiled\n        PerformTask();\n\n        // Subsequent calls, already JIT compiled code is used\n        PerformTask();\n        PerformTask();\n    }\n\n    public static void PerformTask()\n    {\n        // Some task logic here\n        Console.WriteLine("Task performed.");\n    }\n}