using System;\nusing System.DirectoryServices.Protocols;\nusing System.Text;\n\npublic class OctetStringConverter\n{\n    public static byte[] ConvertToOctetString(string input)\n    {\n        // Encode the string to a byte array (UTF-8 encoding)\n        byte[] stringBytes = Encoding.UTF8.GetBytes(input);\n\n        // Use BerConverter to wrap the byte array into an OctetString\n        byte[] octetString = BerConverter.Encode("{O}", stringBytes);\n\n        return octetString;\n    }\n\n    public static void Main()\n    {\n        string inputString = "YourInputString";\n        byte[] octetString = ConvertToOctetString(inputString);\n\n        // Print the result as a hex string for verification\n        Console.WriteLine(BitConverter.ToString(octetString));\n    }\n}