// Example for BotDetect CAPTCHA in ASP.NET\nusing System;\nusing System.Web.UI;\n\npublic partial class Default : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n    }\n\n    protected void ValidateCaptcha(object sender, EventArgs e)\n    {\n        bool isHuman = Captcha.Validate(CaptchaCodeTextBox.Text);\n        CaptchaCodeTextBox.Text = null;\n        ResultLabel.Text = isHuman ? "CAPTCHA passed!" : "CAPTCHA failed!";\n    }\n}