// C# Web API to get localized strings from database\n[Route("api/Localization/GetDbResources")]\npublic class LocalizationController : ApiController\n{\n    [HttpGet]\n    public IHttpActionResult GetDbResources(string culture)\n    {\n        // Replace with actual database call\n        var resources = db.LocalizationResources\n                          .Where(r => r.Culture == culture)\n                          .ToDictionary(r => r.Key, r => r.Value);\n        return Ok(resources);\n    }\n}