using Castle.DynamicProxy;\nusing System;\n\nclass Program\n{\n    static void Main()\n    {\n        ProxyGenerator generator = new ProxyGenerator();\n        CustomInterceptor interceptor = new CustomInterceptor();\n\n        IPerson personProxy = generator.CreateInterfaceProxyWithTarget<IPerson>(new Person { Name = "John" }, interceptor);\n\n        personProxy.Greet();\n    }\n}