using System;\nusing System.Net;\nusing System.IO;\n\npublic class AspxToHtmlConverter\n{\n    public static void Main(string[] args)\n    {\n        string[] urls = new string[]\n        {\n            "http://example.com/page1.aspx",\n            "http://example.com/page2.aspx",\n            "http://example.com/page3.aspx"\n        };\n\n        string outputDirectory = @"C:\StaticHtmlPages\";\n\n        foreach (string url in urls)\n        {\n            SavePageAsHtml(url, outputDirectory);\n        }\n\n        Console.WriteLine("Conversion completed!");\n    }\n\n    private static void SavePageAsHtml(string url, string outputDirectory)\n    {\n        using (WebClient client = new WebClient())\n        {\n            try\n            {\n                string htmlContent = client.DownloadString(url);\n                Uri uri = new Uri(url);\n                string fileName = Path.Combine(outputDirectory, uri.AbsolutePath.Replace("/", "_") + ".html");\n\n                if (!Directory.Exists(outputDirectory))\n                {\n                    Directory.CreateDirectory(outputDirectory);\n                }\n\n                File.WriteAllText(fileName, htmlContent);\n                Console.WriteLine($"Saved {url} as {fileName}");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Failed to save {url}: {ex.Message}");\n            }\n        }\n    }\n}