private void StreamPDFReport(string ReportPath, HttpContext context)\n{\n    context.Response.Buffer = true; // Changed to true to use Response.Flush()\n    context.Response.Clear();\n    context.Response.ClearContent();\n    context.Response.ClearHeaders();        \n\n    // Set the appropriate ContentType.\n    context.Response.ContentType = "application/pdf";\n    context.Response.AddHeader("Content-Disposition", "inline; filename=Report.pdf");\n    context.Response.Cache.SetCacheability(HttpCacheability.NoCache);        \n\n    // Write the file directly to the HTTP content output stream.\n    byte[] fileBytes = System.IO.File.ReadAllBytes(ReportPath);\n    context.Response.AddHeader("Content-Length", fileBytes.Length.ToString());\n    context.Response.BinaryWrite(fileBytes);\n    context.Response.Flush(); // Flush the response to ensure it's sent to the client\n    HttpContext.Current.ApplicationInstance.CompleteRequest();\n    //context.Response.End(); // Not recommended to use Response.End()\n}