using Aspose.Words;\nusing Aspose.Words.Tables;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new document\n        Document doc = new Document();\n        DocumentBuilder builder = new DocumentBuilder(doc);\n\n        // Set up the document layout\n        builder.PageSetup.PaperSize = PaperSize.A6;\n        builder.PageSetup.Orientation = Orientation.Landscape;\n\n        // Add a table for the ID card layout\n        Table table = builder.StartTable();\n        builder.InsertCell();\n        builder.CellFormat.Width = 200;\n\n        // Add content to the ID card\n        builder.Writeln("Company Name");\n        builder.Writeln("Employee Name: John Doe");\n        builder.Writeln("Employee ID: 12345");\n        builder.Writeln("Department: IT");\n\n        // End the table\n        builder.EndTable();\n\n        // Save the document\n        doc.Save("IDCard.docx");\n\n        // Print the document\n        doc.Print();\n    }\n}