public class YourController : Controller\n{\n    private static DateTime _lastCheck = DateTime.MinValue;\n\n    public JsonResult CheckForUpdates()\n    {\n        using (var context = new YourDbContext())\n        {\n            var newRecords = context.YourTable\n                .Where(x => x.CreatedDate > _lastCheck)\n                .ToList();\n\n            if (newRecords.Any())\n            {\n                _lastCheck = DateTime.Now;\n                return Json(new { hasNewRecords = true, records = newRecords }, JsonRequestBehavior.AllowGet);\n            }\n            \n            return Json(new { hasNewRecords = false }, JsonRequestBehavior.AllowGet);\n        }\n    }\n}