using (var context = new MyDataContext())\n{\n    var query = from customer in context.Customers\n                join address in context.Addresses on customer.ID equals address.CustomerID\n                where address.City == "SomeCity"\n                select new { customer.FirstName, customer.LastName, address.Street, address.City };\n\n    var sql = query.ToString();\n    // SQL generated: SELECT [FirstName], [LastName], [Street], [City] FROM [Customers] INNER JOIN [Addresses] ON [Customers].[ID] = [Addresses].[CustomerID] WHERE [Addresses].[City] = @p0\n}