using System;\nusing System.Drawing;\n\npublic class ImageCropper\n{\n    public static Bitmap CropWhiteSpace(Bitmap bmp)\n    {\n        int w = bmp.Width;\n        int h = bmp.Height;\n\n        int left = w, top = h, right = 0, bottom = 0;\n\n        // Find the bounds of the non-white area\n        for (int y = 0; y < h; y++)\n        {\n            for (int x = 0; x < w; x++)\n            {\n                Color pixel = bmp.GetPixel(x, y);\n                if (pixel.ToArgb() != Color.White.ToArgb())\n                {\n                    if (x < left) left = x;\n                    if (y < top) top = y;\n                    if (x > right) right = x;\n                    if (y > bottom) bottom = y;\n                }\n            }\n        }\n\n        // Create a new bitmap with the cropped area\n        int croppedWidth = right - left + 1;\n        int croppedHeight = bottom - top + 1;\n        Bitmap croppedBmp = new Bitmap(croppedWidth, croppedHeight);\n\n        using (Graphics g = Graphics.FromImage(croppedBmp))\n        {\n            g.DrawImage(bmp, new Rectangle(0, 0, croppedWidth, croppedHeight), new Rectangle(left, top, croppedWidth, croppedHeight), GraphicsUnit.Pixel);\n        }\n\n        return croppedBmp;\n    }\n\n    public static void Main(string[] args)\n    {\n        Bitmap originalBmp = (Bitmap)Image.FromFile("path_to_your_image.jpg");\n        Bitmap croppedBmp = CropWhiteSpace(originalBmp);\n        croppedBmp.Save("path_to_save_cropped_image.jpg");\n    }\n}