using System;\nusing System.Linq;\nusing System.Data;\n\npublic class Customer\n{\n    public string Name { get; set; }\n    public string FirstName { get; set; }\n    public string Address { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Create a DataSet with a DataTable\n        DataTable table = new DataTable("tab_data");\n        table.Columns.Add("Name", typeof(string));\n        table.Columns.Add("FirstName", typeof(string));\n        table.Columns.Add("Address", typeof(string));\n        \n        // Add some sample data\n        table.Rows.Add("Smith", "John", "123 Main St");\n        \n        // Convert DataTable to a list of Customer objects\n        var customers = from DataRow row in table.Rows\n                        select new Customer\n                        {\n                            Name = row.Field<string>("Name"),\n                            FirstName = row.Field<string>("FirstName"),\n                            Address = row.Field<string>("Address")\n                        };\n        \n        // Output the results\n        foreach (var customer in customers)\n        {\n            Console.WriteLine($"Name: {customer.Name}, FirstName: {customer.FirstName}, Address: {customer.Address}");\n        }\n    }\n}