using UnitsNet;\nusing UnitsNet.Units;\n\npublic class Program\n{\n    public static void Main()\n    {\n        var m1 = Mass.FromPounds(123.00);\n        var m2 = Volume.FromLiters(123.00);\n\n        var m1InKilograms = m1.ToUnit(MassUnit.Kilogram);\n        var density = Density.FromPoundsPerGallon(7.0);\n        var m2InPounds = m2 * density;\n\n        Console.WriteLine($"m1 in Kilograms: {m1InKilograms}");\n        Console.WriteLine($"m2 in Pounds: {m2InPounds}");\n    }\n}