protected void uc_TenantList_CustomButtonCallback(object sender, ASPxGridViewCustomButtonCallbackEventArgs e)\n{\n    Tenant tenant = (Tenant)uc_TenantList.GetGridView().GetRow(e.VisibleIndex);\n\n    switch (e.ButtonID)\n    {\n        case "btn_show":\n            ShowRow(tenant);\n            break;\n        case "btn_edit":\n            EditRow(tenant);\n            break;\n        case "btn_delete":\n            DeleteRow(tenant.Id);\n            break;\n        default:\n            break;\n    }\n\n    // Manually trigger the update of UpdatePanel\n    ScriptManager.GetCurrent(Page).RegisterAsyncPostBackControl(UpdatePanel1);\n    UpdatePanel1.Update();\n}\n\nprivate void EditRow(Tenant tenant)\n{\n    uc_TenantDetails.SetTenantData(cBLL.GetTenant(tenant.Id));\n    // UpdatePanel1.Update(); // Moved to the event handler\n}