using Excel = Microsoft.Office.Interop.Excel;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new Excel application instance\n        Excel.Application excelApp = new Excel.Application();\n        excelApp.Visible = true;\n\n        // Add a new workbook\n        Excel.Workbook workbook = excelApp.Workbooks.Add();\n        Excel.Worksheet worksheet = workbook.Sheets[1];\n\n        // Access the PageSetup object\n        Excel.PageSetup pageSetup = worksheet.PageSetup;\n\n        // Set the footer with page number and bold text\n        string footerText = "&\"Arial,Bold\"This is bold footer text - Page &P";\n        pageSetup.CenterFooter = footerText;\n\n        // Optionally, save and clean up\n        // workbook.SaveAs(@"C:\path\to\your\file.xlsx");\n        // workbook.Close();\n        // excelApp.Quit();\n    }\n}