using System;\nusing System.Collections.Generic;\n\npublic static class LinkedListExtensions\n{\n    public static LinkedList<TOutput> ConvertAll<TInput, TOutput>(this LinkedList<TInput> list, Converter<TInput, TOutput> converter)\n    {\n        if (list == null)\n        {\n            throw new ArgumentNullException(nameof(list));\n        }\n\n        if (converter == null)\n        {\n            throw new ArgumentNullException(nameof(converter));\n        }\n\n        LinkedList<TOutput> outputList = new LinkedList<TOutput>();\n        foreach (TInput item in list)\n        {\n            outputList.AddLast(converter(item));\n        }\n\n        return outputList;\n    }\n}