using System.Collections.ObjectModel;\nusing System.Windows.Input;\n\npublic class MainWindowViewModel\n{\n    public ObservableCollection<DiceViewModel> DiceViewModels { get; set; }\n    public ICommand RollCommand { get; private set; }\n\n    public MainWindowViewModel()\n    {\n        DiceViewModels = new ObservableCollection<DiceViewModel>();\n        RollCommand = new RelayCommand(param => RollAllDice());\n    }\n\n    private void RollAllDice()\n    {\n        foreach (var diceViewModel in DiceViewModels)\n        {\n            diceViewModel.Roll();\n        }\n    }\n}