<% if (info.CurrentStatus != OrderStatus.Received && info.CurrentStatus != OrderStatus.Processing) { %>\n    <td>\n        <%= Ajax.ActionLink(\n            "Reprocess Order", \n            "Reprocess",\n            new { generatedId = info.GeneratedId },\n            new AjaxOptions\n            {\n                UpdateTargetId = "success" + info.GeneratedId,\n                OnSuccess = "reprocessSuccess"\n            }) %>\n    </td>\n<% } %>\n\n<td>\n    <div id="success<%= info.GeneratedId %>"></div>\n</td>