using System;\nusing System.Diagnostics;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string tempFilePath = Path.Combine(Path.GetTempPath(), Path.GetRandomFileName());\n\n        using (var tempFile = new FileStream(\n            tempFilePath,\n            FileMode.Create,\n            FileAccess.ReadWrite,\n            FileShare.ReadWrite | FileShare.Delete,\n            4096,\n            FileOptions.DeleteOnClose))\n        {\n            // Write data to the file\n            using (StreamWriter writer = new StreamWriter(tempFile))\n            {\n                writer.WriteLine("Temporary data");\n            }\n\n            // Ensure the data is flushed to the file\n            tempFile.Flush(true);\n            \n            // Launch the associated application\n            Process.Start(new ProcessStartInfo\n            {\n                FileName = tempFilePath,\n                UseShellExecute = true\n            });\n        }\n\n        // At this point, the file handle is closed and the file will be deleted when the other process closes it\n    }\n}