using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    const string LibpostalLib = "libpostal.so";\n\n    [DllImport(LibpostalLib, CallingConvention = CallingConvention.Cdecl)]\n    public static extern void libpostal_setup();\n\n    [DllImport(LibpostalLib, CallingConvention = CallingConvention.Cdecl)]\n    public static extern void libpostal_teardown();\n\n    [DllImport(LibpostalLib, CallingConvention = CallingConvention.Cdecl)]\n    public static extern IntPtr libpostal_parse_address(string address, IntPtr options);\n\n    [DllImport(LibpostalLib, CallingConvention = CallingConvention.Cdecl)]\n    public static extern void libpostal_address_parser_response_destroy(IntPtr response);\n\n    static void Main(string[] args)\n    {\n        libpostal_setup();\n\n        var address = "23 Street n. 213 new york ny";\n        var result = libpostal_parse_address(address, IntPtr.Zero);\n\n        // Handle the result (e.g., marshal it back to C# structures)\n\n        libpostal_address_parser_response_destroy(result);\n        libpostal_teardown();\n    }\n}