using System.Linq.Dynamic.Core;\n\npublic void GetRecordsByTableName(string tableName)\n{\n    if (string.IsNullOrWhiteSpace(tableName) || !IsValidTableName(tableName))\n    {\n        throw new ArgumentException("Invalid table name.", nameof(tableName));\n    }\n\n    var query = $"dataContext.{tableName}";\n    var records = dataContext.ExecuteQuery<object>(query).ToList();\n\n    ViewData["recordsByTableName"] = records;\n}\n\nprivate bool IsValidTableName(string tableName)\n{\n    var allowedTableNames = new List<string> { "Table1", "Table2", "Table3" };\n    return allowedTableNames.Contains(tableName);\n}