using Facebook;\nusing System;\nusing System.Configuration;\n\npublic class FacebookLoginChecker\n{\n    public bool IsUserLoggedIn(string accessToken)\n    {\n        var fb = new FacebookClient(accessToken);\n\n        try\n        {\n            dynamic result = fb.Get("me");\n            return result != null;\n        }\n        catch (FacebookOAuthException)\n        {\n            return false;\n        }\n    }\n}\n\n// Example usage:\nclass Program\n{\n    static void Main()\n    {\n        string accessToken = "user_access_token"; // Replace with the actual access token\n        var loginChecker = new FacebookLoginChecker();\n        bool isLoggedIn = loginChecker.IsUserLoggedIn(accessToken);\n\n        Console.WriteLine("Is user logged in: " + isLoggedIn);\n    }\n}