using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlArticlesFileName = "path_to_your_xml_file.xml";\n        int id = 42; // Example id\n\n        XmlDocument _xmlArticlesDocument = new XmlDocument();\n        _xmlArticlesDocument.Load(xmlArticlesFileName);\n\n        // Use XmlNamespaceManager if you have namespaces in your XML.\n        XmlNamespaceManager namespaceManager = new XmlNamespaceManager(_xmlArticlesDocument.NameTable);\n        \n        // XPath query to find the page element with the specific id\n        string xPathQuery = $"//mediawiki/page[id='{id}']";\n\n        XmlNode node = _xmlArticlesDocument.DocumentElement.SelectSingleNode(xPathQuery, namespaceManager);\n\n        if (node != null)\n        {\n            Console.WriteLine("Page found:");\n            Console.WriteLine(node.OuterXml);\n        }\n        else\n        {\n            Console.WriteLine("Page not found.");\n        }\n    }\n}