using System;\nusing System.Collections.Generic;\n\npublic class Program\n{\n    public static int BinarySearch<T>(List<T> list, Func<T, int> condition)\n    {\n        int left = 0;\n        int right = list.Count - 1;\n\n        while (left <= right)\n        {\n            int middle = (left + right) / 2;\n            int result = condition(list[middle]);\n\n            if (result == 0)\n                return middle; // This is the desired item\n            else if (result < 0)\n                right = middle - 1; // The desired item must be to the left\n            else if (result > 0)\n                left = middle + 1; // The desired item must be to the right\n        }\n\n        return -1; // The desired item can't be in the list\n    }\n\n    public static void Main()\n    {\n        List<int> sortedList = new List<int> { 1, 3, 5, 7, 9, 11, 13, 15 };\n\n        // Example condition: find the first item greater than 10\n        int index = BinarySearch(sortedList, item =>\n        {\n            if (item == 10)\n                return 0;\n            else if (item < 10)\n                return 1;\n            else\n                return -1;\n        });\n\n        if (index != -1)\n            Console.WriteLine("Item found at index: " + index);\n        else\n            Console.WriteLine("Item not found");\n    }\n}