using System;\nusing System.IO;\nusing System.Linq;\nusing System.Timers;\n\nclass Program\n{\n    private static Timer timer;\n\n    static void Main()\n    {\n        string directoryPath = @"C:\wallpapers";\n\n        timer = new Timer(15 * 60 * 1000); // 15 minutes in milliseconds\n        timer.Elapsed += (sender, e) => SelectRandomFile(directoryPath);\n        timer.Start();\n\n        // Prevent the application from exiting\n        Console.WriteLine("Press [Enter] to exit the program.");\n        Console.ReadLine();\n    }\n\n    private static void SelectRandomFile(string directoryPath)\n    {\n        string[] files = Directory.GetFiles(directoryPath);\n\n        if (files.Length == 0)\n        {\n            Console.WriteLine("No files found in the directory.");\n            return;\n        }\n\n        Random random = new Random();\n        int randomIndex = random.Next(files.Length);\n        string randomFile = files[randomIndex];\n\n        Console.WriteLine("Randomly selected file: " + randomFile);\n    }\n}