public static class HtmlHelpers\n{\n    public static MvcHtmlString CustomValidationSummary(this HtmlHelper html)\n    {\n        if (!html.ViewData.ModelState.IsValid)\n        {\n            var errors = html.ViewData.ModelState.Values.SelectMany(v => v.Errors).Select(e => e.ErrorMessage).ToList();\n            var ul = new TagBuilder("ul");\n            foreach (var error in errors)\n            {\n                var li = new TagBuilder("li");\n                var img = new TagBuilder("img");\n                img.MergeAttribute("src", "/path/to/your/image.png");\n                li.InnerHtml = img.ToString(TagRenderMode.SelfClosing) + " " + error;\n                ul.InnerHtml += li.ToString();\n            }\n            return MvcHtmlString.Create(ul.ToString());\n        }\n        return MvcHtmlString.Empty;\n    }\n}