using System;\nusing System.Management;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        string deviceId = @"USB\VID_XXXX&PID_XXXX\XXXXXXXXXXXX"; // Replace with your device ID\n        CycleUsbDevice(deviceId);\n    }\n\n    static void CycleUsbDevice(string deviceId)\n    {\n        var searcher = new ManagementObjectSearcher(@"Select * From Win32_PnPEntity");\n        foreach (var device in searcher.Get())\n        {\n            var deviceObject = (ManagementObject)device;\n            if (deviceObject["DeviceID"].ToString() == deviceId)\n            {\n                try\n                {\n                    Console.WriteLine("Disabling device...");\n                    deviceObject.InvokeMethod("Disable", new object[] { false });\n                    Thread.Sleep(5000); // Wait for 5 seconds before re-enabling\n\n                    Console.WriteLine("Enabling device...");\n                    deviceObject.InvokeMethod("Enable", new object[] { false });\n                    Console.WriteLine("Device cycled successfully.");\n                }\n                catch (Exception ex)\n                {\n                    Console.WriteLine("An error occurred: " + ex.Message);\n                }\n                break;\n            }\n        }\n    }\n}