using System;\nusing System.Collections;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class MyObject\n{\n    public int Id { get; set; }\n    public string Code { get; set; }\n    public string Description { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<MyObject> myList = new List<MyObject>\n        {\n            new MyObject { Id = 1, Code = "A1", Description = "Item1" },\n            new MyObject { Id = 2, Code = "B2", Description = "Item2" },\n            new MyObject { Id = 3, Code = "C3", Description = "Item3" }\n        };\n\n        Hashtable hashtable = myList\n            .ToDictionary(item => item.Description, item => item.Id)\n            .ToHashtable();\n\n        // Serialize to JSON (using Newtonsoft.Json for example)\n        string json = Newtonsoft.Json.JsonConvert.SerializeObject(hashtable);\n        Console.WriteLine(json);\n    }\n}\n\npublic static class Extensions\n{\n    public static Hashtable ToHashtable<TKey, TValue>(this IDictionary<TKey, TValue> dictionary)\n    {\n        Hashtable hashtable = new Hashtable();\n        foreach (var pair in dictionary)\n        {\n            hashtable.Add(pair.Key, pair.Value);\n        }\n        return hashtable;\n    }\n}