using System;\nusing System.Data;\nusing System.IO;\nusing System.Net;\nusing System.Xml;\n\npublic string GetArtistThumbnail(string artistName)\n{\n    var request = WebRequest.Create("http://ws.audioscrobbler.com/2.0/?method=artist.getinfo&artist=" + artistName + "&api_key=" + APIKey) as HttpWebRequest;\n\n    using (var response = request.GetResponse() as HttpWebResponse)\n    {\n        using (var stream = response.GetResponseStream())\n        {\n            var xmlDoc = new XmlDocument();\n            xmlDoc.Load(stream);\n\n            // Remove the nested artist element to avoid the exception\n            XmlNodeList artistNodes = xmlDoc.SelectNodes("//artist");\n            foreach (XmlNode artistNode in artistNodes)\n            {\n                XmlNode nestedArtistNode = artistNode.SelectSingleNode("artist");\n                if (nestedArtistNode != null)\n                {\n                    artistNode.RemoveChild(nestedArtistNode);\n                }\n            }\n\n            var ds = new DataSet();\n            using (var reader = new XmlNodeReader(xmlDoc))\n            {\n                ds.ReadXml(reader);\n            }\n\n            // Process the DataSet as needed\n        }\n    }\n\n    return "";\n}