private void FlowLayoutPanel_Resize(object sender, EventArgs e)\n{\n    // Suspend the layout logic of the FlowLayoutPanel\n    flowLayoutPanel1.SuspendLayout();\n\n    // Resize all controls inside the FlowLayoutPanel\n    foreach (Control control in flowLayoutPanel1.Controls)\n    {\n        control.Width = flowLayoutPanel1.ClientSize.Width - control.Margin.Horizontal - SystemInformation.VerticalScrollBarWidth;\n    }\n\n    // Resume the layout logic and force the control to layout its child controls\n    flowLayoutPanel1.ResumeLayout(true);\n    flowLayoutPanel1.PerformLayout();\n}