using System;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Text;\n\npublic class UdpListener\n{\n    private const int Port = 11000;\n\n    public static void StartListening()\n    {\n        UdpClient udpClient = new UdpClient(Port);\n        IPEndPoint endPoint = new IPEndPoint(IPAddress.Any, Port);\n\n        while (true)\n        {\n            byte[] bytes = udpClient.Receive(ref endPoint);\n            string message = Encoding.UTF8.GetString(bytes);\n            Console.WriteLine($"Received broadcast: {message}");\n            \n            // Establish a TCP connection to get the actual message\n            TcpClient tcpClient = new TcpClient(message, 12000);\n            NetworkStream stream = tcpClient.GetStream();\n            \n            byte[] buffer = new byte[1024];\n            int bytesRead = stream.Read(buffer, 0, buffer.Length);\n            string actualMessage = Encoding.UTF8.GetString(buffer, 0, bytesRead);\n\n            Console.WriteLine($"Received actual message: {actualMessage}");\n\n            stream.Close();\n            tcpClient.Close();\n        }\n    }\n}