using Oracle.ManagedDataAccess.Client;\nusing System.Data;\n\npublic void PopulateTempTable(List<MyObject> data)\n{\n    string connectionString = "your_connection_string";\n    using (OracleConnection connection = new OracleConnection(connectionString))\n    {\n        connection.Open();\n        using (OracleCommand command = connection.CreateCommand())\n        {\n            command.CommandText = "INSERT INTO temp_table (id, name) VALUES (:id, :name)";\n            command.ArrayBindCount = data.Count;\n            command.Parameters.Add(":id", OracleDbType.Int32).Value = data.Select(d => d.Id).ToArray();\n            command.Parameters.Add(":name", OracleDbType.Varchar2).Value = data.Select(d => d.Name).ToArray();\n            command.ExecuteNonQuery();\n        }\n    }\n}