using System;\nusing System.Linq;\nusing System.Linq.Dynamic.Core;\nusing YourNamespace.DataAccess; // Replace with your actual namespace\n\npublic class DynamicLinqDemo\n{\n    public void ExecuteDynamicQuery()\n    {\n        // Assuming you have a DataContext or similar for LLBLGen\n        var context = new YourDataContext(); // Replace with your actual data context\n\n        string entityName = "Customer";\n        string[] selectedFields = new string[] { "Number", "City", "CountryEntity.Name" };\n\n        // Build the select clause\n        string selectClause = "new (" + string.Join(", ", selectedFields) + ")";\n\n        // Dynamically generate the query\n        var query = context.Customer\n                           .Select(selectClause);\n\n        // Execute the query\n        var result = query.ToList();\n\n        // Output the result\n        foreach (var item in result)\n        {\n            Console.WriteLine(item);\n        }\n    }\n}