using System;\nusing System.Net.Mail;\nusing System.Net.Mime;\nusing System.Text;\nusing System.IO;\n\npublic class EmailProcessor\n{\n    public static void Main()\n    {\n        // Assume 'message' is your MailMessage object obtained from the POP3 server\n        MailMessage message = GetMailMessageFromPop3Server();\n\n        foreach (Attachment attachment in message.Attachments)\n        {\n            // Decode the attachment\n            MemoryStream ms = new MemoryStream();\n            attachment.ContentStream.CopyTo(ms);\n            byte[] attachmentBytes = ms.ToArray();\n\n            // Get the file size\n            long fileSize = attachmentBytes.Length;\n\n            // Output the file size\n            Console.WriteLine($"Attachment: {attachment.Name}, Size: {fileSize} bytes");\n        }\n    }\n\n    private static MailMessage GetMailMessageFromPop3Server()\n    {\n        // Implementation for fetching the MailMessage from POP3 server goes here\n        // This is just a placeholder\n        return new MailMessage();\n    }\n}