using System;\nusing System.Collections.Generic;\n\npublic class StringSearcher\n{\n    private List<string> stringList;\n    private HashSet<string> stringSet;\n    private bool useHashSet;\n\n    public StringSearcher(IEnumerable<string> strings, int threshold = 10)\n    {\n        var stringList = new List<string>(strings);\n        useHashSet = stringList.Count > threshold;\n\n        if (useHashSet)\n        {\n            stringSet = new HashSet<string>(stringList);\n        }\n        else\n        {\n            this.stringList = stringList;\n        }\n    }\n\n    public bool Contains(string item)\n    {\n        if (useHashSet)\n        {\n            return stringSet.Contains(item);\n        }\n        else\n        {\n            return stringList.Contains(item);\n        }\n    }\n}