using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class AesEncryption\n{\n    public static string DecryptData(string encryptedData, string key)\n    {\n        byte[] fullCipher = Convert.FromBase64String(encryptedData);\n\n        byte[] iv = new byte[16];\n        byte[] cipherText = new byte[fullCipher.Length - iv.Length];\n\n        Array.Copy(fullCipher, iv, iv.Length);\n        Array.Copy(fullCipher, iv.Length, cipherText, 0, cipherText.Length);\n\n        using (Aes aes = Aes.Create())\n        {\n            aes.Key = Encoding.UTF8.GetBytes(key);\n            aes.IV = iv;\n            aes.Padding = PaddingMode.PKCS7;\n            aes.Mode = CipherMode.CBC;\n\n            using (var decryptor = aes.CreateDecryptor(aes.Key, aes.IV))\n            {\n                using (var ms = new System.IO.MemoryStream(cipherText))\n                {\n                    using (var cs = new CryptoStream(ms, decryptor, CryptoStreamMode.Read))\n                    {\n                        using (var sr = new System.IO.StreamReader(cs))\n                        {\n                            return sr.ReadToEnd();\n                        }\n                    }\n                }\n            }\n        }\n    }\n}\n\n// Usage\nstring key = "your-encryption-key"; // Must be 16, 24, or 32 bytes\nstring encryptedData = "Base64StringFromJavascript";\nstring decryptedData = AesEncryption.DecryptData(encryptedData, key);\nConsole.WriteLine(decryptedData);