// Foxit SDK usage example (assuming necessary setup and licensing)\n   using Foxit.PDF;\n   using Foxit.PDF.Annotations;\n\n   PDFDoc document = new PDFDoc("path/to/your/document.pdf");\n   document.Load();\n   PDFPage page = document.GetPage(0);\n   \n   // Example of adding a text annotation\n   TextMarkup annotation = new TextMarkup();\n   annotation.SetContent("This is a text annotation");\n   page.AddAnnotation(annotation);\n   \n   document.Save("path/to/your/annotated_document.pdf");