// Add the Syncfusion PDF Viewer component to your form\n   using Syncfusion.Windows.Forms.PdfViewer;\n\n   PdfViewerControl pdfViewer = new PdfViewerControl();\n   pdfViewer.Dock = DockStyle.Fill;\n   this.Controls.Add(pdfViewer);\n\n   // Load a PDF document\n   pdfViewer.Load("path/to/your/document.pdf");\n\n   // Example of adding an annotation programmatically\n   PdfLoadedDocument loadedDocument = new PdfLoadedDocument("path/to/your/document.pdf");\n   PdfLoadedPage loadedPage = loadedDocument.Pages[0] as PdfLoadedPage;\n   PdfFreeTextAnnotation textAnnotation = new PdfFreeTextAnnotation(new RectangleF(100, 100, 200, 50), "This is a text annotation");\n   loadedPage.Annotations.Add(textAnnotation);\n   loadedDocument.Save("path/to/your/annotated_document.pdf");