using (SqlConnection connection = new SqlConnection(ConnectionString))\n{\n    string ReceiveString = /* your SQL query here */;\n    SqlCommand command = new SqlCommand(ReceiveString, connection);\n    connection.Open();\n\n    SqlDataReader reader = command.ExecuteReader();\n\n    while (reader.Read())\n    {\n        SqlXml sqlXml = reader.GetSqlXml(reader.GetOrdinal("MessageBodyXml"));\n        string xmlContent = sqlXml.Value;\n\n        SccmAction sa = DeserializeObject<SccmAction>(xmlContent);\n        IDelivery iD = (IDelivery)sa; // Assuming SccmAction implements IDelivery\n    }\n\n    reader.Close();\n}\n\npublic static T DeserializeObject<T>(string xml)\n{\n    XmlSerializer xs = new XmlSerializer(typeof(T));\n    using (StringReader stringReader = new StringReader(xml))\n    {\n        return (T)xs.Deserialize(stringReader);\n    }\n}