using System;\nusing System.IO;\nusing System.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string directoryPath = @"C:\Your\Directory\Path";\n\n        var mostRecentFile = new DirectoryInfo(directoryPath)\n                                .GetFiles()\n                                .OrderByDescending(f => f.LastWriteTime)\n                                .FirstOrDefault();\n\n        if (mostRecentFile != null)\n        {\n            Console.WriteLine("Most recent file: " + mostRecentFile.FullName);\n            Console.WriteLine("Last modified: " + mostRecentFile.LastWriteTime);\n        }\n        else\n        {\n            Console.WriteLine("No files found in the directory.");\n        }\n    }\n}