public class Deduplication\n{\n    // Dictionary to keep track of created PRQT headers\n    private static Dictionary<string, bool> createdHeaders = new Dictionary<string, bool>();\n\n    public static bool ShouldCreatePRQT(string primaryKey)\n    {\n        if (!createdHeaders.ContainsKey(primaryKey))\n        {\n            createdHeaders[primaryKey] = true;\n            return true;\n        }\n        return false;\n    }\n}